import React, { Component } from "react";
import './css/login.css';
import Cookies from "js-cookie";
import Login from '../Header-Footer/header-home.js';

class Popup extends React.Component {

  constructor(props){
    super(props);

    this.state = {
      userInputPersonNummer: '',
      personNummer: '',
      data: []
    }
  }

  componentDidMount() {
    fetch('https://csgo-rent-api-lrib5.ondigitalocean.app/API/User/readUser.php')
        .then(res => res.json())
        .then(json => {
            const { personNummer, data} = json;
            this.setState({ personNummer, data});
        }).catch((err) => {
            console.log(err);
        });
}


  handleChange = (event) => {
    this.setState({ userInputPersonNummer: event.target.value });
  }

  handleSubmit = (userInputPersonNummer, data) => {
